/*
 * 动态添加script标题 
 * 
 * */ 
function appendInfo(href) {
    let reg = new RegExp('http\:|https\:');
    let src = '';
    if (reg.test(href)) {
        src = href;
    }
    else {
        src = publicHref + href;
    }
    document.write(`<script src='${src}'><\/script>`);
}
